mapper.select:
select * from t_gfxx where yxbz = '1'

    order by #{orderBy}


   and gfsh = #{gfsh}


    and gsdm = #{gsdm}


    and gfmc = #{gfmc}

limit 1

        select * from t_gfxx where yxbz = '1'
        
            
                #{params.xfs[${index}]}
            
            or xfid is null)
        
        
			and gsdm = #{params.gsdm}
		
		
			and gfmc = #{params.gfmc}
		
		
			and gfsh = #{params.nsrsbh}
		
        
            and lrsj >= #{params.lrsjq}
        
        
            and lrsj <
                date_add(str_to_date(#{params.lrsjz},'%Y-%m-%d'),INTERVAL 1 day)
        
		order by xgsj desc
