	and gsdm = #{gsdm}


	and ddh = #{ddh}


	and tqm = #{tqm}


	and jylsh = #{jylsh}


	and sqlsh = #{sqlsh}

limit 1

select * from t_jyxxsq where yxbz = '1'

	
		#{tqm}
	


	and gsdm = #{gsdm}


	order by #{orderBy}

