mapper.select:
select * from kplsvo where yxbz = '1'

    order by #{orderBy}

limit 1

select * from kplsvo where yxbz = '1' and fpzldm = '12' and fphm is not null and fphm != ''

    and a.gsdm = #{gsdm}


    and kprq >= ${kprqq}


    and kprq < ${kprqz}


    and fpczlxdm = '11'


    and (fpczlxdm = '12' or fpczlxdm = '13')


    and kprq like concat('%',#{kprq},'%')


    and fpczlxdm = #{fpczlxdm}

select * from kplsvo where yxbz = '1' and fpzldm = '12' AND fpczlxdm = #{params.fpczlxdm} and fphm is not null

    and a.gsdm = #{params.gsdm}



        #{params.xfs[${index}].id} 

    or a.xfid is null)



        #{params.skps[${index}].id} 

    or a.skpid is null)


    and ddh like concat('%',#{params.ddh},'%')


    and fphm like concat('%',#{params.fphm},'%')


    and gfmc like concat('%',#{params.gfmc},'%')


    and kprq >= ${params.kprqq}


    and kprq < ${params.kprqz}


    and jylssj >= ${params.jyrqq}


    and jylssj < ${params.jyrqz}


 order by kprq desc
