mapper.select:
select * from t_skp where yxbz = '1'

	and kpddm = #{kpddm}


	and kpdmc = #{kpdmc}


	and gsdm = #{gsdm}


	and id != #{id}


	and xfid != #{xfid}

limit 1

select s.*,x.xfmc from t_skp s, t_xf x where s.xfid = x.id and s.yxbz
= '1'

	and s.gsdm = #{params.gsdm}


	and s.xfid = #{params.xfid}


	and s.kpdmc like concat('%',#{params.kpdmc},'%')


	order by lrsj desc

select * from t_skp where yxbz = '1' and id in (select kpdid from t_cszb where yxbz='1' and csz='是' and csid = (select id from t_csb where csm = 'sfzczt') and kpdid in (SELECT id FROM t_skp WHERE yxbz='1'
and xfid = #{xfid}
and xfid = (select min(id) from t_xf where xfsh=#{xfsh} and yxbz='1')
))
