mapper.select:
select * from t_smtq where yxbz = '1'

    and ddh = #{ddh}


    and gsdm = #{gsdm}

limit 1

select a.* from t_smtq a,t_jyls b,t_kpls c where a.fpzt='07' 
   and a.yxbz='1' and a.ddh = b.ddh and b.fpzldm='12' and b.fpczlxdm='11' and b.yxbz='1'
   and b.djh = c.djh and c.fpzldm = '12' and c.fpczlxdm='11' and c.yxbz='1'

   and a.gsdm = #{params.gsdm}
   and c.gsdm = #{params.gsdm}



        #{params.xfs[${index}].id} 

    or c.xfid is null)



        #{params.skps[${index}].id} 

    or c.skpid is null)


   and a.ddh = #{params.ddh}


       and a.gfmc like CONCAT('%',#{params.gfmc},'%') 


       and a.jylssj between #{params.tqrqq} and #{params.tqrqz}

order by a.jylssj
