mapper.select:
select * from t_yhdyk where yxbz = '1'

    order by #{orderBy}

limit 1

select a.dybt,b.id,b.dyfs,b.sjhm,b.email,b.openid from t_dyzl a,t_yhdyk b
    where a.id=b.dybtid and b.yxbz='1' and a.yxbz='1'
    and b.yhid=#{params.yhid}

select count(*) fpunm,sum(t.zsje+t.fsje) hjje,sum(t.zsse+t.fsse) hjse from (select lrry,lrsj, case when fpczlxdm in('11','14') then hjje else 0 end zsje, case when fpczlxdm in('12','13') then hjje else 0 end fsje, case when fpczlxdm in('11','14') then hjse else 0 end zsse, case when fpczlxdm in('12','13') then hjse else 0 end fsse from t_kpls where yxbz='1' and fphm is not NULL
and lrry = #{lrry}
and kprq like CONCAT('%',#{kprq},'%')
) t
